import React, { useState } from "react";
import portrait from "../imgs/default-pp.jpg";
import "../Dashboard.css";

export default function Dashboard() {
  const [selectedText, setSelectedText] = useState(null);

  const handleTextClick = (text) => {
    setSelectedText(text);
  };

  const getBackgroundColor = (text) => {
    switch (text) {
      case "Invest":
        return "pink";
      default:
        return "";
    }
  };

  return (
    <div className="dashboard">
      <h2 className="page-title">Placeholder</h2>
      <div
        className={`container-middle${
          selectedText ? " " + getBackgroundColor(selectedText) : ""
        }`}
      >
        Dashboard
      </div>
      <div className="profile">
        <h4 className="name">Alexandru Culea</h4>
        <img src={portrait} alt=" "></img>
      </div>
      <div className="containers-left">
        <div className="first-container">
          <h3 className="first-container-text">Dashboard</h3>
        </div>
        <div className="second-container">
          <h4
            className="second-container-text"
            onClick={() => handleTextClick("Invest")}
          >
            Invest
          </h4>
          <h4
            className="second-container-text"
            onClick={() => handleTextClick("Following List")}
          >
            Following List
          </h4>
          <h4
            className="second-container-text"
            onClick={() => handleTextClick("Porfolios")}
          >
            Porfolios
          </h4>
        </div>
        <div className="third-container">
          <h4
            className="third-container-text"
            onClick={() => handleTextClick("Profile")}
          >
            Profile
          </h4>
          <h4
            className="third-container-text"
            onClick={() => handleTextClick("Log out")}
          >
            Log out
          </h4>
        </div>
      </div>
    </div>
  );
}
