import React, { useState, useEffect } from "react";
import portrait from "../imgs/default-pp.jpg";
import axios from "axios";
import "../Dashboard.css";
import { Link, useNavigate } from "react-router-dom";

export default function DashboardInvest() {
  const [isLoggedIn, setIsLoggedIn] = useState();
  const [modal, setModal] = useState(false);
  const [username, setUsername] = useState("");
  const [riskValue, setRiskValue] = useState("");
  const navigate = useNavigate();
  const [balanceWarning, setBalanceWarning] = useState("");
  const [riskWarning, setRiskWarning] = useState("");
  const [selectedButton, setSelectedButton] = useState(null);
  const [balanceInput, setBalanceInput] = useState("");
  const [riskInputA, setRiskInputA] = useState("");
  const [riskInputB, setRiskInputB] = useState("");
  const [balanceReturn, setBalanceReturn] = useState("");
  const [filteredData, setFilteredData] = useState([]);
  const [assetSymbol, setAssetSymbol] = useState("");
  const [searchState, setSearchState] = useState({
    companyName: "",
    hasSearched: false,
  });
  const [searchResult, setSearchResult] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:3002").then((res) => {
      if (res.data.valid) {
        setUsername(res.data.username);
      }
    });
  }, []);

  useEffect(() => {
    const checkLoginStatus = async () => {
      try {
        const response = await fetch("http://localhost:3002/", {
          method: "GET",
          credentials: "include",
        });
        const data = await response.json();
        setIsLoggedIn(data.valid);
      } catch (error) {
        console.error("Error checking login status:", error);
      }
    };
    checkLoginStatus();
  }, []);

  useEffect(() => {
    if (isLoggedIn === false) {
      navigate("/login");
    }
  }, [isLoggedIn, navigate]);

  const toggleModal = () => {
    setModal(!modal);
  };

  useEffect(() => {
    if (modal) {
      document.body.classList.add("active-modal");
    } else {
      document.body.classList.remove("active-modal");
    }
  }, [modal]);

  const apiKey = "SbUhzMlpiU94dp9UtJGKlPs59R6DBpGi";
  const query = "AA";
  const searchApiUrl = `https://financialmodelingprep.com/api/v3/search?query=${query}&apikey=${apiKey}`;

  useEffect(() => {
    if (!searchState.hasSearched || !searchState.companyName) return;

    fetch(searchApiUrl)
      .then((response) => response.json())
      .then((data) => {
        const foundItem = data.find((item) =>
          item.name
            .toLowerCase()
            .includes(searchState.companyName.toLowerCase())
        );
        setSearchResult(foundItem ? [foundItem] : []);
        setAssetSymbol(foundItem ? foundItem.symbol : "");
        setSearchState({
          ...searchState,
          hasSearched: false,
        });
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setSearchResult(null);
      });
  }, [searchApiUrl, searchState]);

  const handleInputChange = (event) => {
    setSearchState({
      ...searchState,
      companyName: event.target.value,
    });
  };

  const handleSearch = () => {
    setSearchState({
      ...searchState,
      hasSearched: true,
    });
  };

  const handleButtonClick = (buttonNumber) => {
    setSelectedButton(buttonNumber);
    setBalanceInput("");
    setRiskInputA("");
    setRiskInputB("");
    setBalanceReturn(0);
  };

  const fromDate = "2023-10-10";
  const toDate = "2023-12-10";

  useEffect(() => {
    if (!assetSymbol) return;
    console.log(assetSymbol.toString());
    const histroyApiUrl = `https://financialmodelingprep.com/api/v3/historical-chart/4hour/${assetSymbol}?from=${fromDate}&to=${toDate}&apikey=${apiKey}`;
    console.log(histroyApiUrl);
    fetch(histroyApiUrl)
      .then((response) => response.json())
      .then((data) => {
        const filtered = data.filter((item) => {
          const date = new Date(item.date);
          return date.getHours() === 15;
        });

        setFilteredData(filtered);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }, [assetSymbol, fromDate, toDate]);

  const handleSubmitOneAsset = async () => {
    console.log(filteredData.map((item) => item.close));
    const balance = Number(balanceInput);
    let riskA = Number(riskInputA);

    // Validate balance
    if (isNaN(balance) || balance <= 0) {
      setBalanceWarning("Please insert a valid balance value for Asset A.");
      return;
    } else {
      setBalanceWarning("");
    }

    // Validate riskA
    if (isNaN(riskA) || riskA < 1 || riskA > 10) {
      setRiskWarning(
        "Please insert a risk value between 1 and 10 for Asset A."
      );
      return;
    } else {
      setRiskWarning("");
    }

    try {
      await axios.post("http://localhost:3002/save-risk", {
        username: username,
        riskValue: riskInputA,
      });
      setRiskValue(riskInputA);
    } catch (error) {
      console.error("Error saving risk value:", error.message);
    }

    const closesA = filteredData.map((item) => item.close);

    const returnsA = calculateDailyReturns(closesA);

    const meanReturnA = calculateMeanReturn(returnsA);

    const deviationsA = calculateDeviations(returnsA, returnsA);

    const covarianceA = calculateCovariance(deviationsA, deviationsA);

    let bestReturnHigherRisk = {
      portfolioReturn: -Infinity,
      portfolioVariance: -Infinity,
      weights: { weightA: 0 },
    };

    for (let weightA = 1; weightA <= 10; weightA += 0.5) {
      const totalWeight = weightA;
      const normalizedWeightA = weightA / totalWeight;

      const portfolioReturn = normalizedWeightA * meanReturnA;

      const portfolioVariance = normalizedWeightA ** 2 * (riskInputA / 10) ** 2;

      if (
        portfolioReturn > bestReturnHigherRisk.portfolioReturn &&
        portfolioVariance > bestReturnHigherRisk.portfolioVariance
      ) {
        bestReturnHigherRisk = {
          portfolioReturn,
          portfolioVariance,
          weights: {
            weightA: Math.min(
              Math.max(Math.round(normalizedWeightA * 20) / 2, 1),
              10
            ),
          },
        };
      }
    }

    const userWeightA = (riskInputA / 10) * 10;

    console.log("Mean Return A:", meanReturnA);
    console.log("Covariance between Asset A and Asset B:", covarianceA);
    console.log("Best Weights for Higher Return:", {
      weightA: bestReturnHigherRisk.weights.weightA,
    });
    console.log("Your weights:", {
      weightA: userWeightA,
    });

    const portfolioReturn = userWeightA * meanReturnA;
    console.log(
      `Portfolio Return based on your weight: ${parseFloat(
        portfolioReturn.toFixed(2)
      )}%`
    );

    const portfolioBalanceReturn =
      Number(balanceInput) + (balanceInput * portfolioReturn) / 100;

    setBalanceReturn(portfolioBalanceReturn);

    const portfolioReturnBasedOnBestWeights =
      bestReturnHigherRisk.weights.weightA * meanReturnA;
    console.log(
      `Portfolio Return based on best weights: ${parseFloat(
        portfolioReturnBasedOnBestWeights.toFixed(2)
      )}%`
    );

    const portfolioVariance =
      bestReturnHigherRisk.weights.weightA ** 2 * (riskInputA / 10) ** 2;
    console.log("Portfolio Variance:", portfolioVariance);
  };

  const handleSubmitTwoAssets = async () => {
    const balance = Number(balanceInput);
    let riskA = Number(riskInputA);
    let riskB = Number(riskInputB);

    // Validate balance
    if (isNaN(balance) || balance <= 0) {
      setBalanceWarning("Please insert a valid balance value for Asset A.");
      return;
    } else {
      setBalanceWarning("");
    }

    // Validate riskA
    if (isNaN(riskA) || riskA < 1 || riskA > 10) {
      setRiskWarning(
        "Please insert a risk value between 1 and 10 for Asset A."
      );
      return;
    } else {
      setRiskWarning("");
    }

    // Validate riskB
    if (isNaN(riskB) || riskB < 1 || riskB > 10) {
      setRiskWarning(
        "Please insert a risk value between 1 and 10 for Asset B."
      );
      return;
    } else {
      setRiskWarning("");
    }
    // try {
    //   await axios.post("http://localhost:3002/save-risk", {
    //     username: username,
    //     riskValue: risk,
    //   });
    //   setRiskValue(risk);
    // } catch (error) {
    //   console.error("Error saving risk value:", error.message);
    // }

    const closesA = [
      22636.46875, 23117.85938, 23032.77734, 23078.72852, 23031.08984,
      23774.56641, 22840.13867, 23139.2832, 23723.76953, 23471.87109,
      23449.32227, 23331.84766, 22955.66602, 22760.10938, 23264.29102,
      22939.39844, 21819.03906, 21651.18359, 21870.875, 21788.20313,
      21808.10156, 22220.80469, 24307.8418, 23623.47461, 24565.60156,
      24641.27734, 24327.64258, 24829.14844, 24436.35352, 24188.84375,
      23947.49219, 23198.12695, 23175.375, 23561.21289, 23522.87109,
      23147.35352, 23646.55078, 23475.4668, 22362.67969, 22353.34961,
      22435.51367, 22429.75781, 22219.76953, 21718.08008, 20363.02148,
      20187.24414, 20632.41016, 22163.94922, 24197.5332, 24746.07422,
      24375.96094, 25052.78906, 27423.92969, 26965.87891, 28038.67578,
      27767.23633, 28175.81641, 27307.4375, 28333.97266, 27493.28516,
      27494.70703, 27994.33008, 27139.88867, 27268.13086, 28348.44141,
      28033.5625, 28478.48438, 28411.03516, 28199.30859, 27790.2207,
      28168.08984, 28177.98438, 28044.14063, 27925.85938, 27947.79492,
      28333.05078, 29652.98047, 30235.05859, 30139.05273, 30399.06641,
      30485.69922, 30318.49609, 30315.35547, 29445.04492, 30397.55273,
