const form = document.getElementById("form");
const email = document.getElementById("email");
const password = document.getElementById("password");
const confirmPassword = document.getElementById("confirmPassword"); // Updated ID
const success = document.getElementById("success");
const error = document.getElementById("error");

form.addEventListener("submit", (event) => {
  event.preventDefault();

  // Check if password and confirmPassword match
  if (password.value !== confirmPassword.value) {
    error.style.display = "block";
    error.innerText = "Password and confirm password do not match";
    success.style.display = "none";
    return;
  }

  const register = {
    email: email.value,
    password: password.value,
    confirmPassword: confirmPassword.value, // Include confirmPassword in the request
  };

  fetch("/api/register", {
    method: "POST",
    body: JSON.stringify(register),
    headers: {
      "Content-Type": "application/json",
    },
  })
    .then((res) => res.json())
    .then((data) => {
      if (data.status == "error") {
        success.style.display = "none";
        error.style.display = "block";
        error.innerText = data.error;
      } else {
        error.style.display = "none";
        success.style.display = "block";
        success.innerText = data.success;

        setTimeout(() => {
          window.location.href = "/";
        }, 2000);
      }
    })
    .catch((error) => {
      console.error("Error during registration:", error);
    });
});
