import React, { useState, useEffect } from "react";
import Axios from "axios";
import { useNavigate } from "react-router-dom";

export default function VerifyEmail() {
  const [verified, setVerified] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const navigateTo = useNavigate();

  useEffect(() => {
    const urlSearchParams = new URLSearchParams(window.location.search);
    const token = urlSearchParams.get("token");

    Axios.post("http://localhost:3002/verify-email", {
      token: token,
    })
      .then((response) => {
        if (response.data.message === "Email verified successfully") {
          setVerified(true);
        } else {
          setErrorMessage("Invalid or expired token");
        }
      })
      .catch((error) => {
        console.log("Error during email verification: ", error);
        setErrorMessage("Error during email verification. Please try again.");
      });
  }, []);

  if (verified) {
    return (
      <div className="verify-email-page">
        <div className="verify-email">
          <a href="/" className="page-title">
            Placeholder
          </a>
          <div className="wrapper">
            <h1>Email Verified</h1>
            <p>Your email address has been verified. You can now log in.</p>
            <button
              type="button"
              className="btn-login"
              onClick={() => navigateTo("/login")}
            >
              Log In
            </button>
          </div>
        </div>
      </div>
    );
  } else {
    return (
      <div className="verify-email-page">
        <div className="verify-email">
          <a href="/" className="page-title">
            Placeholder
          </a>
          <div className="wrapper">
            <h1>Verify Email</h1>
            <p>
              Please click on the link in the email we sent you to verify your
              email.
            </p>
            <span>{errorMessage}</span>
          </div>
        </div>
      </div>
    );
  }
}
