import React, { useState, useEffect } from "react";
import portrait from "../imgs/default-pp.jpg";
import axios from "axios";
import "../Dashboard.css";
import { Link, useNavigate } from "react-router-dom";

export default function DashboardProfile() {
  const [isLoggedIn, setIsLoggedIn] = useState();
  const [modal, setModal] = useState(false);
  const [username, setUsername] = useState("");
  const navigate = useNavigate();
  const [firstNameInput, setFirstName] = useState("");
  const [secondNameInput, setSecondName] = useState("");
  const [countryInput, setCountry] = useState("");
  const [currencyInput, setCurrency] = useState("");
  const [pronunInput, setPronun] = useState("");

  useEffect(() => {
    axios.get("http://localhost:3002").then((res) => {
      if (res.data.valid) {
        setUsername(res.data.username);
      }
    });
  }, []);

  useEffect(() => {
    const checkLoginStatus = async () => {
      try {
        const response = await fetch("http://localhost:3002/", {
          method: "GET",
          credentials: "include",
        });
        const data = await response.json();
        setIsLoggedIn(data.valid);
      } catch (error) {
        console.error("Error checking login status:", error);
      }
    };
    checkLoginStatus();
  }, []);

  useEffect(() => {
    if (isLoggedIn === false) {
      navigate("/login");
    }
  }, [isLoggedIn, navigate]);

  const toggleModal = () => {
    setModal(!modal);
  };

  useEffect(() => {
    if (modal) {
      document.body.classList.add("active-modal");
    } else {
      document.body.classList.remove("active-modal");
    }
  }, [modal]);

  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    if (storedUsername) {
      setUsername(storedUsername);
    }
  }, []);

  const handleSaveUser = async () => {
    try {
      const response = await axios.post(
        "http://localhost:3002/save-data-profile",
        {
          username: username,
          firstName: firstNameInput,
          secondName: secondNameInput,
          country: countryInput,
          currency: currencyInput,
          pronun: pronunInput,
        }
      );

      if (response.status === 200) {
        console.log("User data updated successfully");
        setFirstName(firstNameInput);
        setSecondName(secondNameInput);
        setCountry(countryInput);
        setCurrency(currencyInput);
        setPronun(pronunInput);
      } else {
        console.error("Unexpected response status: ", response.status);
      }
    } catch (error) {
      if (!error.response) {
        console.error("No response received from server.");
      } else if (error.response.status === 401) {
        console.error("Unauthorized access.");
      } else if (error.response.status === 403) {
        console.error("Forbidden access.");
      } else {
        console.error("Error saving user data: ", error.message);
      }
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");
    setIsLoggedIn(false);
    axios
      .get("http://localhost:3002/logout")
      .then(() => {
        console.log("User logged out successfully");
      })
      .catch((error) => {
        console.error("Error logging out:", error);
      });
  };

  return (
    <div className="dashboard">
      <div className="page-title">
        <Link to="/" className="custom-link">
          <h2>Placeholder</h2>
        </Link>
      </div>
      <div className="container-middle">
        <div className="profile-form">
          <input type="hidden" name="username" value={username} />
          <label htmlFor="firstName">
            First Name:
            <input
              type="text"
              placeholder="firstName"
              value={firstNameInput}
              onChange={(e) => setFirstName(e.target.value)}
            />
          </label>
          <br />

          <label htmlFor="secondName">
            Second Name:
            <input
              type="text"
              placeholder="secondName"
              value={secondNameInput}
              onChange={(e) => setSecondName(e.target.value)}
            />
          </label>
          <br />

          <label htmlFor="country">
            Country:
            <input
              type="text"
              placeholder="country"
              value={countryInput}
              onChange={(e) => setCountry(e.target.value)}
            />
          </label>
          <br />

          <label htmlFor="currency">
            Currency:
            <input
              type="text"
              placeholder="currency"
              value={currencyInput}
              onChange={(e) => setCurrency(e.target.value)}
            />
          </label>
          <br />

          <label htmlFor="pronun">
            Pronunciation:
            <input
              type="text"
              placeholder="pronun"
              value={pronunInput}
              onChange={(e) => setPronun(e.target.value)}
            />
          </label>
          <br />

          <button type="submit" onClick={handleSaveUser}>
            Save
          </button>
        </div>
      </div>
      <div className="profile">
        <div className="username">{username}</div>
        <img src={portrait} alt=" "></img>
      </div>
      <div className="containers-left">
        <div className="first-container">
          <Link to="/dashboard" className="custom-link">
            <h3 className="first-container-text">Dashboard</h3>
          </Link>
        </div>
        <div className="second-container">
          <Link to="/dashboard/invest" className="custom-link">
            <h4 className="second-container-text">Invest</h4>
          </Link>
          <Link to="/dashboard/watchlist" className="custom-link">
            <h4 className="second-container-text">Watchlist</h4>
          </Link>
          <Link to="/dashboard/portfolios" className="custom-link">
            <h4 className="second-container-text">Portfolios</h4>
          </Link>
        </div>
        <div className="third-container">
          <Link to="/dashboard/profile" className="custom-link">
            <h4
              className="third-container-text"
              style={{ backgroundColor: "rgb(161, 161, 161)" }}
            >
              Profile
            </h4>
          </Link>
          <h4 className="third-container-text" onClick={toggleModal}>
            Log out
          </h4>
        </div>
        <div className="modal-container">
          {modal && (
            <div className="modal">
              <div onClick={toggleModal} className="overlay"></div>
              <div className="modal-content">
                <h2>Are you sure you want to log out?</h2>
                <Link to="/" className="custom-link">
                  <button className="modal-yes-btn" onClick={handleLogout}>
                    Yes
                  </button>
                </Link>
                <button className="modal-no-btn" onClick={toggleModal}>
                  No
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
