import React, { useState, useEffect } from "react";
import portrait from "../imgs/default-pp.jpg";
import axios from "axios";
import "../Dashboard.css";
import { Link, useNavigate } from "react-router-dom";

export default function DashboardInvest() {
  const [isLoggedIn, setIsLoggedIn] = useState();
  const [modal, setModal] = useState(false);
  const [username, setUsername] = useState("");
  const [balance, setBalance] = useState(0);
  const [risk, setRisk] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:3002").then((res) => {
      if (res.data.valid) {
        setUsername(res.data.username);
        setBalance(res.data.balance);
        setRisk(res.data.risk);
      }
    });
  }, []);

  useEffect(() => {
    const checkLoginStatus = async () => {
      try {
        const response = await fetch("http://localhost:3002/", {
          method: "GET",
          credentials: "include",
        });
        const data = await response.json();
        setIsLoggedIn(data.valid);
      } catch (error) {
        console.error("Error checking login status:", error);
      }
    };
    checkLoginStatus();
  }, []);

  useEffect(() => {
    if (isLoggedIn === false) {
      navigate("/login");
    }
  }, [isLoggedIn, navigate]);

  const toggleModal = () => {
    setModal(!modal);
  };

  if (modal) {
    document.body.classList.add("active-modal");
  } else {
    document.body.classList.remove("active-modal");
  }

  const validateRiskInput = (risk) => {
    if (isNaN(risk) || risk === "") {
      setRiskInputWarning("Risk must be a number");
      return false;
    }
    if (risk === 0) {
      setRiskInputWarning("Risk must not be 0");
      return false;
    }
    if (risk < 1 || risk > 10) {
      setRiskInputWarning("Risk must be between 1 and 10");
      return false;
    }
    setRiskInputWarning("");
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!validateRiskInput(riskInput)) {
      return;
    }
    try {
      axios.put("http://localhost:5000/update-user", {
        username,
        balance,
        risk: parseInt(riskInput),
      });
      alert("User updated successfully");
    } catch (err) {
      console.error(err);
      alert("Error updating user");
    }

    if (Number(balance) === 0) {
      setBalanceInputWarning("Please insert a valid balance value.");
    } else {
      setBalanceInputWarning("");
      setBalance((prevBalance) => prevBalance + Number(balanceInput));
      setBalanceInput("");
    }

    if (Number(risk) === 0) {
      setRiskInputWarning("Please insert a valid risk value.");
    } else if (Number(risk) > 10 || Number(risk) < 1) {
      setRiskInputWarning("Please add a risk value between 1 and 10.");
    } else {
      setRiskInputWarning("");
      setRisk((prevRisk) => prevRisk + Number(riskInput));
      setRiskInput("");
    }
  };

  const [balanceInput, setBalanceInput] = useState("");
  const [riskInput, setRiskInput] = useState("");
  const [balanceInputWarning, setBalanceInputWarning] = useState("");
  const [riskInputWarning, setRiskInputWarning] = useState("");

  const displayAccountValue = function (property) {
    if (property === "balance" && Number(balance) === 0) {
      return "Your balance: N/A";
    } else if (property === "risk" && Number(risk) === 0) {
      return "Your risk: N/A";
    }
  };

  const nextDays = function (day) {
    let valuesPerDay = [];
    if (risk > 0 && balance > 0) {
      for (let i = 0; i < day; i++) {
        valuesPerDay.push(Math.floor(Math.random() * day + 1));
      }

      for (const values of valuesPerDay) {
        if (values > 5) {
          setBalance((prevBalance) => prevBalance + values * 10);
        } else if (values < 5) {
          setBalance((prevBalance) => prevBalance - values * 10);
        }
      }
    } else if (risk > 0 && (balance === 0 || balance < 0)) {
      setBalanceInputWarning(
        "Please insert a valid balance value. Your balance value is: " + balance
      );
    } else if (balance > 0 && (risk === 0 || risk < 0)) {
      setRiskInputWarning(
        "Please insert a valid risk value. Your risk value is: " + risk
      );
    } else {
      setBalanceInputWarning(
        "Please insert a valid balance value. Your balance value is: " +
          balance +
          "\nPlease insert a valid risk value. Your risk value is: " +
          risk
      );
    }
  };

  const pastDays = function (day) {
    let valuesPerPastDays = [];
    for (let i = 0; i < day; i++) {
      valuesPerPastDays.push(Math.floor(Math.random() * day + 1));
    }

    let averageValues =
      valuesPerPastDays.reduce((acc, value) => acc + value, 0) / day;

    alert(
      `The average value of the past ${day} days is: ${averageValues.toFixed(
        2
      )}`
    );
  };

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");
    setIsLoggedIn(false);
    axios
      .get("http://localhost:3002/logout")
      .then(() => {
        console.log("User logged out successfully");
      })
      .catch((error) => {
        console.error("Error logging out:", error);
      });
  };

  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    if (storedUsername) {
      setUsername(storedUsername);
    }
  }, []);

  useEffect(() => {
    if (username) {
      localStorage.setItem("username", username);
    }
  }, [username]);

  const saveRiskValue = async (username, riskValue) => {
    try {
      const response = await axios.post("http://localhost:3002/save-risk", {
        username: username,
        riskValue: riskValue,
      });

      if (response.status === 200) {
        console.log("Risk value saved successfully");
      }
    } catch (error) {
      console.error("Error saving risk value:", error.message);
    }
  };

  // Use the saveRiskValue function when the risk value changes
  useEffect(() => {
    if (isLoggedIn) {
      saveRiskValue(username, risk);
    }
  }, [isLoggedIn, username, risk]);

  return (
    <div className="dashboard">
      <div className="page-title">
        <Link to="/" className="custom-link">
          <h2>Placeholder</h2>
        </Link>
      </div>
      <div className="container-middle">
        <div className="balance-value">{displayAccountValue("balance")}</div>
        <div className="risk-value">{displayAccountValue("risk")}</div>
        <input
          type="input"
          placeholder="Balance"
          value={balanceInput}
          onChange={(e) => setBalanceInput(e.target.value)}
        />
        <p className="warning">{balanceInputWarning}</p>
        <input
          type="input"
          placeholder="Risk"
          value={riskInput}
          onChange={(e) => setRiskInput(e.target.value)}
        />
        <p className="warning">{riskInputWarning}</p>
        <button className="submit-button" onClick={handleSubmit}>
          Submit
        </button>
        <button className="next-day-button" onClick={() => nextDays(1)}>
          Next Day
        </button>
        <button className="next-7-days" onClick={() => nextDays(7)}>
          Next 7 Days
        </button>
        <button className="past-7-days" onClick={() => pastDays(7)}>
          Past 7 Days
        </button>
      </div>
      <div className="profile">
        <div className="username">{username}</div>
        <img src={portrait} alt=" "></img>
      </div>
      <div className="containers-left">
        <div className="first-container">
          <Link to="/dashboard" className="custom-link">
            <h3 className="first-container-text">Dashboard</h3>
          </Link>
        </div>
        <div className="second-container">
          <Link to="/dashboard/invest" className="custom-link">
            <h4
              className="second-container-text"
              style={{ backgroundColor: "rgb(161, 161, 161)" }}
            >
              Invest
            </h4>
          </Link>
          <Link to="/dashboard/watchlist" className="custom-link">
            <h4 className="second-container-text">Watchlist</h4>
          </Link>
          <Link to="/dashboard/portfolios" className="custom-link">
            <h4 className="second-container-text">Porfolios</h4>
          </Link>
        </div>
        <div className="third-container">
          <Link to="/dashboard/profile" className="custom-link">
            <h4 className="third-container-text">Profile</h4>
          </Link>
          <h4 className="third-container-text" onClick={toggleModal}>
            Log out
          </h4>
        </div>
        <div className="modal-container">
          {modal && (
            <div className="modal">
              <div onClick={toggleModal} className="overlay"></div>
              <div className="modal-content">
                <h2>Are you sure you want to log out?</h2>
                <Link to="/" className="custom-link">
                  <button className="modal-yes-btn" onClick={handleLogout}>
                    Yes
                  </button>
                </Link>
                <button className="modal-no-btn" onClick={toggleModal}>
                  No
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
