import React, { useEffect, useState, useRef } from "react";
import Axios from "axios";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../Login.css";

const Login = () => {
  const navigateTo = useNavigate();

  const [loginStatus, setLoginStatus] = useState("");
  const [statusHolder, setstatusHolder] = useState("message");

  const loginUserNameRef = useRef();
  const loginPasswordRef = useRef();

  const loginUser = async (event) => {
    event.preventDefault();
    const loginUserName = loginUserNameRef.current.value.trim();
    const loginPassword = loginPasswordRef.current.value.trim();

    if (
      loginUserName.value.trim() === "" ||
      loginPassword.value.trim() === ""
    ) {
      setLoginStatus("Please enter both username and password");
      return;
    }

    try {
      const response = await Axios.post("http://localhost:3002/login", {
        LoginUserName,
        LoginPassword,
      });

      if (response.data.message) {
        setLoginStatus("Credentials error");
      } else {
        localStorage.setItem("isLoggedIn", "true");
        navigateTo("/");
      }
    } catch (error) {
      console.error("Error during login: ", error);
      setLoginStatus("An error occurred during login. Please try again.");
    }
  };

  useEffect(() => {
    if (loginStatus !== "") {
      setstatusHolder("showMessage");
      setTimeout(() => {
        setstatusHolder("message");
      }, 4000);
    }
  }, [loginStatus]);

  axios.defaults.withCredentials = true;

  return (
    <div className="login-page">
      <div className="login">
        <a href="/" className="page-title">
          Placeholder
        </a>
        <div className="wrapper">
          <form id="form">
            <h1>Login</h1>
            <div className="mb-3 input-box">
              <input
                type="username"
                className="form-control"
                id="username"
                autoComplete="off"
                placeholder="Username"
              />
            </div>
            <div className="mb-3 input-box">
              <input
                type="password"
                className="form-control"
                id="password"
                autoComplete="off"
                placeholder="Password"
              />
            </div>
            <div className="mb-3">
              <div className="forgot">
                <a href="*">Forgot password?</a>
              </div>
            </div>
            <div className="mb-3">
              <button type="submit" className="btn-login" onClick={loginUser}>
                Login
              </button>
            </div>
            <div className="register-text mb-3">
              <p>
                Don't have an account?
                <a href="/register" className="link-register">
                  Sign-up
                </a>
              </p>
              <span className={statusHolder}>{loginStatus}</span>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
