import React, { useState, useEffect } from "react";
import portrait from "../imgs/default-pp.jpg";
import axios from "axios";
import "../Dashboard.css";
import { Link, useNavigate } from "react-router-dom";
import Chart from "chart.js/auto";

export default function DashboardWatchlist() {
  const [isLoggedIn, setIsLoggedIn] = useState();
  const [modal, setModal] = useState(false);
  const [username, setUsername] = useState("");
  const [data, setData] = useState(null);
  const navigate = useNavigate();
  const [chartInstance, setChartInstance] = useState(null);
  const [assetSymbol, setAssetSymbol] = useState("");
  const [searchState, setSearchState] = useState({
    companyName: "",
    hasSearched: false,
  });
  const [searchResult, setSearchResult] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:3002").then((res) => {
      if (res.data.valid) {
        setUsername(res.data.username);
      }
    });
  }, []);

  useEffect(() => {
    const checkLoginStatus = async () => {
      try {
        const response = await fetch("http://localhost:3002/", {
          method: "GET",
          credentials: "include",
        });
        const data = await response.json();
        setIsLoggedIn(data.valid);
      } catch (error) {
        console.error("Error checking login status:", error);
      }
    };
    checkLoginStatus();
  }, []);

  useEffect(() => {
    if (isLoggedIn === false) {
      navigate("/login");
    }
  }, [isLoggedIn, navigate]);

  const toggleModal = () => {
    setModal(!modal);
  };

  useEffect(() => {
    if (modal) {
      document.body.classList.add("active-modal");
    } else {
      document.body.classList.remove("active-modal");
    }
  }, [modal]);

  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    if (storedUsername) {
      setUsername(storedUsername);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");
    setIsLoggedIn(false);
    axios
      .get("http://localhost:3002/logout")
      .then(() => {
        console.log("User logged out successfully");
      })
      .catch((error) => {
        console.error("Error logging out:", error);
      });
  };
  const apiKey = "SbUhzMlpiU94dp9UtJGKlPs59R6DBpGi";
  const searchApiUrl = `https://financialmodelingprep.com/api/v3/stock/list?apikey=${apiKey}`;

  useEffect(() => {
    if (!searchStateA.hasSearched || !searchStateA.companyName) return;

    fetch(searchApiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        if (!data) {
          console.error("Error: Data is null");
          setSearchResultA(null);
          return;
        }

        const foundItem = data.find((item) => {
          if (!item || !item.name) return false;
          return item.name
            .toLowerCase()
            .includes(searchStateA.companyName.toLowerCase());
        });

        setSearchResultA(foundItem ? [foundItem] : []);
        setAssetSymbolA(foundItem ? foundItem.symbol : "");
        setSearchStateA({
          ...searchStateA,
          hasSearched: false,
        });
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setSearchResultA(null);
      });
  }, [searchApiUrl, searchStateA]);

  const stockSymbol = "AAPL";
  const fromDate = "2023-03-23";
  const toDate = "2024-03-23";
  const apiUrl = `https://financialmodelingprep.com/api/v3/historical-chart/4hour/${stockSymbol}?from=${fromDate}&to=${toDate}&apikey=${apiKey}`;

  useEffect(() => {
    fetch(apiUrl)
      .then((response) => response.json())
      .then((data) => {
        const filteredData = data.filter((item) => {
          const date = new Date(item.date);
          return date;
        });
        setData(filteredData);
        console.log(filteredData);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }, [apiUrl]);

  useEffect(() => {
    if (data) {
      if (chartInstance) {
        chartInstance.destroy();
      }
      drawChart();
    }
  }, [data]);

  const drawChart = () => {
    const ctx = document.getElementById("myChart").getContext("2d");
    const newChartInstance = new Chart(ctx, {
      type: "line",
      data: {
        labels: data.map((item) => new Date(item.date).toLocaleDateString()),
        datasets: [
          {
            label: "Close Value",
            data: data.map((item) => item.close),
            borderColor: "rgba(255, 99, 132, 1)",
            borderWidth: 1,
          },
        ],
      },
      options: {
        scales: {
          y: {
            beginAtZero: false,
            ticks: {
              font: {
                size: 12,
              },
            },
          },
          x: {
            display: true,
            ticks: {
              font: {
                size: 12,
              },
            },
            grid: {
              display: false,
            },
          },
        },
      },
    });
    setChartInstance(newChartInstance);
  };

  return (
    <div className="dashboard">
      <div className="page-title">
        <Link to="/" className="custom-link">
          <h2>Placeholder</h2>
        </Link>
      </div>
      <div className="container-middle">
        <input type="text" placeholder="Search for an Asset" />
        <canvas id="myChart"></canvas>
      </div>
      <div className="profile">
        <div className="username">{username}</div>
        <img src={portrait} alt=" "></img>
      </div>
      <div className="containers-left">
        <div className="first-container">
          <Link to="/dashboard" className="custom-link">
            <h3 className="first-container-text">Dashboard</h3>
          </Link>
        </div>
        <div className="second-container">
          <Link to="/dashboard/invest" className="custom-link">
            <h4 className="second-container-text">Invest</h4>
          </Link>
          <Link to="/dashboard/watchlist" className="custom-link">
            <h4
              className="second-container-text"
              style={{ backgroundColor: "rgb(161, 161, 161)" }}
            >
              Watchlist
            </h4>
          </Link>
          <Link to="/dashboard/portfolios" className="custom-link">
            <h4 className="second-container-text">Portfolios</h4>
          </Link>
        </div>
        <div className="third-container">
          <Link to="/dashboard/profile" className="custom-link">
            <h4 className="third-container-text">Profile</h4>
          </Link>
          <h4 className="third-container-text" onClick={toggleModal}>
            Log out
          </h4>
        </div>
        <div className="modal-container">
          {modal && (
            <div className="modal">
              <div onClick={toggleModal} className="overlay"></div>
              <div className="modal-content">
                <h2>Are you sure you want to log out?</h2>
                <Link to="/" className="custom-link">
                  <button className="modal-yes-btn" onClick={handleLogout}>
                    Yes
                  </button>
                </Link>
                <button className="modal-no-btn" onClick={toggleModal}>
                  No
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
