import React, { useState, useEffect } from "react";
import portrait from "../imgs/default-pp.jpg";
import axios from "axios";
import "../Dashboard.css";
import { Link, useNavigate } from "react-router-dom";

export default function DashboardInvest() {
  const [isLoggedIn, setIsLoggedIn] = useState();
  const [modal, setModal] = useState(false);
  const [username, setUsername] = useState("");
  const [riskValueA, setRiskValueA] = useState("");
  const [riskValueB, setRiskValueB] = useState("");
  const [balanceA, setBalanceA] = useState(0);
  const [balanceB, setBalanceB] = useState(0);
  const [finalBalance, setFinalBalance] = useState(0);
  const navigate = useNavigate();
  const [balanceWarning, setBalanceWarning] = useState("");
  const [riskWarning, setRiskWarning] = useState("");

  useEffect(() => {
    axios.get("http://localhost:3002").then((res) => {
      if (res.data.valid) {
        setUsername(res.data.username);
      }
    });
  }, []);

  useEffect(() => {
    const checkLoginStatus = async () => {
      try {
        const response = await fetch("http://localhost:3002/", {
          method: "GET",
          credentials: "include",
        });
        const data = await response.json();
        setIsLoggedIn(data.valid);
      } catch (error) {
        console.error("Error checking login status:", error);
      }
    };
    checkLoginStatus();
  }, []);

  useEffect(() => {
    if (isLoggedIn === false) {
      navigate("/login");
    }
  }, [isLoggedIn, navigate]);

  const toggleModal = () => {
    setModal(!modal);
  };

  useEffect(() => {
    if (modal) {
      document.body.classList.add("active-modal");
    } else {
      document.body.classList.remove("active-modal");
    }
  }, [modal]);

  const handleBalanceChange = (e, balanceState) => {
    const value = parseFloat(e.target.value);
    if (isNaN(value) || value <= 0) {
      setBalanceWarning("Please insert a valid balance value.");
      balanceState.current = 0;
    } else {
      setBalanceWarning("");
      balanceState.current = value;
    }
  };

  const handleRiskChange = (e, riskState) => {
    const value = parseFloat(e.target.value);
    if (isNaN(value) || value < 1 || value > 10) {
      setRiskWarning("Please insert a risk value between 1 and 10.");
      riskState.current = 0;
    } else {
      setRiskWarning("");
      riskState.current = value;
    }
  };

  const handleSubmit = () => {
    const balanceAValue = balanceA.current;
    const balanceBValue = balanceB.current;
    const riskAValue = riskValueA.current;
    const riskBValue = riskValueB.current;

    if (balanceAValue <= 0 || balanceBValue <= 0) {
      setBalanceWarning("Please insert valid balance values.");
      return;
    }

    if (
      riskAValue <= 0 ||
      riskAValue > 10 ||
      riskBValue <= 0 ||
      riskBValue > 10
    ) {
      setRiskWarning("Please insert valid risk values between 1 and 10.");
      return;
    }

    // Perform calculations with balanceAValue, balanceBValue, riskAValue, and riskBValue
    // ...

    // Set the final balance
    setFinalBalance(calculatedFinalBalance);

    // Clear input fields
    setRiskValueA("");
    setRiskValueB("");
    setBalanceA("");
    setBalanceB("");
  };

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");
    setIsLoggedIn(false);
    axios
      .get("http://localhost:3002/logout")
      .then(() => {
        console.log("User logged out successfully");
      })
      .catch((error) => {
        console.error("Error logging out:", error);
      });
  };

  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    if (storedUsername) {
      setUsername(storedUsername);
    }
  }, []);

  useEffect(() => {
    if (isLoggedIn) {
      getRiskValue(username).then((riskValue) => {
        setRiskValueA(riskValue || 0);
      });
    }
  }, [isLoggedIn, username]);

  const getRiskValue = async (username) => {
    try {
      const response = await axios.post("http://localhost:3002/get-risk", {
        username: username,
      });

      if (response.status === 200) {
        console.log("Risk value fetched successfully:", response.data.risk);
        return response.data.risk;
      }
    } catch (error) {
      console.error("Error fetching risk value:", error.message);
    }
  };

  return (
    <div className="dashboard">
      <div className="page-title">
        <Link to="/" className="custom-link">
          <h2>Placeholder</h2>
        </Link>
      </div>
      <div className="container-middle">
        <div className="balance-value">
          {`Your Balance: ${balanceA.current} + ${balanceB.current}`}
        </div>
        <div className="balance-warning">{balanceWarning}</div>
        <div className="risk-value">{`Your Risk: ${riskValueA} + ${riskValueB}`}</div>
        <div className="risk-warning">{riskWarning}</div>
        <input
          type="input"
          placeholder="Balance A"
          value={balanceA}
          onChange={(e) => handleBalanceChange(e, balanceA)}
        />
        <input
          type="input"
          placeholder="Balance B"
          value={balanceB}
          onChange={(e) => handleBalanceChange(e, balanceB)}
        />
        <input
          type="input"
          placeholder="Risk A"
          value={riskValueA}
          onChange={(e) => handleRiskChange(e, riskValueA)}
        />
        <input
          type="input"
          placeholder="Risk B"
          value={riskValueB}
          onChange={(e) => handleRiskChange(e, riskValueB)}
        />
        <button className="submit-button" onClick={handleSubmit}>
          Submit
        </button>
      </div>
      <div className="profile">
        <div className="username">{username}</div>
        <img src={portrait} alt=" "></img>
      </div>
      <div className="containers-left">
        <div className="first-container">
          <Link to="/dashboard" className="custom-link">
            <h3 className="first-container-text">Dashboard</h3>
          </Link>
        </div>
        <div className="second-container">
          <Link to="/dashboard/invest" className="custom-link">
            <h4
              className="second-container-text"
              style={{ backgroundColor: "rgb(161, 161, 161)" }}
            >
              Invest
            </h4>
          </Link>
          <Link to="/dashboard/watchlist" className="custom-link">
            <h4 className="second-container-text">Watchlist</h4>
          </Link>
          <Link to="/dashboard/portfolios" className="custom-link">
            <h4 className="second-container-text">Portfolios</h4>
          </Link>
        </div>
        <div className="third-container">
          <Link to="/dashboard/profile" className="custom-link">
            <h4 className="third-container-text">Profile</h4>
          </Link>
          <h4 className="third-container-text" onClick={toggleModal}>
            Log out
          </h4>
        </div>
        <div className="modal-container">
          {modal && (
            <div className="modal">
              <div onClick={toggleModal} className="overlay"></div>
              <div className="modal-content">
                <h2>Are you sure you want to log out?</h2>
                <Link to="/" className="custom-link">
                  <button className="modal-yes-btn" onClick={handleLogout}>
                    Yes
                  </button>
                </Link>
                <button className="modal-no-btn" onClick={toggleModal}>
                  No
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
