import React, { useState } from "react";
import portrait from "../imgs/default-pp.jpg";
import "../Dashboard.css";

export default function Dashboard() {
  const [containerMiddleContent, setContainerContent] = useState(null);
  const [activeContainer, setActiveContainer] = useState(null);

  const generateInvestCode = () => {
    return <div>Hello</div>;
  };

  const handleTextClick = (content) => {
    setContainerContent(null);
    setActiveContainer(content);
    switch (content) {
      case "dashboard":
        setContainerContent(null);
        break;
      case "invest":
        setContainerContent(generateInvestCode());
        break;
      case "following-assets":
        setContainerContent(<h4>Following-list1</h4>);
        break;
      case "portfolios":
        setContainerContent(<h4>Portfolios1</h4>);
        break;
      case "profile":
        setContainerContent(<h4>Profile1</h4>);
        break;
      case "log-out":
        setContainerContent(<h4>Log-out1</h4>);
        break;
      default:
        setContainerContent(null);
        break;
    }
  };

  const handleContainerHover = (content) => {
    if (activeContainer !== content) {
      setActiveContainer(content);
    }
  };

  const handleContainerLeave = () => {
    setActiveContainer(null);
  };

  return (
    <div className="dashboard">
      <h2 className="page-title">Placeholder</h2>
      <div className="container-middle">{containerMiddleContent}</div>
      <div className="profile">
        <h4 className="name">Alexandru Culea</h4>
        <img src={portrait} alt=" "></img>
      </div>
      <div className="containers-left">
        <div className="first-container">
          <h3
            className={`first-container-text ${
              activeContainer === "dashboard" ? "active" : ""
            }`}
            onClick={() => handleTextClick("dashboard")}
          >
            Dashboard
          </h3>
        </div>
        <div className="second-container">
          <h4
            className={`second-container-text ${
              activeContainer === "invest" ? "active" : ""
            }`}
            onClick={() => handleTextClick("invest")}
            onMouseOver={() => handleContainerHover("invest")}
            onMouseLeave={handleContainerLeave}
          >
            Invest
          </h4>
          <h4
            className={`second-container-text ${
              activeContainer === "following-assets" ? "active" : ""
            }`}
            onClick={() => handleTextClick("following-assets")}
            onMouseOver={() => handleContainerHover("following-assets")}
            onMouseLeave={handleContainerLeave}
          >
            Followed Assets
          </h4>
          <h4
            className={`second-container-text ${
              activeContainer === "portfolios" ? "active" : ""
            }`}
            onClick={() => handleTextClick("portfolios")}
            onMouseOver={() => handleContainerHover("portfolios")}
            onMouseLeave={handleContainerLeave}
          >
            Porfolios
          </h4>
        </div>
        <div className="third-container">
          <h4
            className={`third-container-text ${
              activeContainer === "profile" ? "active" : ""
            }`}
            onClick={() => handleTextClick("profile")}
            onMouseOver={() => handleContainerHover("profile")}
            onMouseLeave={handleContainerLeave}
          >
            Profile
          </h4>
          <h4
            className={`third-container-text ${
              activeContainer === "log-out" ? "active" : ""
            }`}
            onClick={() => handleTextClick("log-out")}
            onMouseOver={() => handleContainerHover("log-out")}
            onMouseLeave={handleContainerLeave}
          >
            Log out
          </h4>
        </div>
      </div>
    </div>
  );
}
